import React from 'react';
import { Link } from 'react-router-dom';

const QuestionDisplay = ({ generatedQuestions = [] }) => { // Default to empty array
  const handleSaveQuestions = () => {
    if (generatedQuestions.length === 0) return;
    
    const content = generatedQuestions.join('\n');
    const blob = new Blob([content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'generated_questions.txt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  return (
    <div>
      <h2>Generated Questions:</h2>
      {generatedQuestions.length > 0 ? (
        <div className="question-box">
          <ol>
            {generatedQuestions.map((question, index) => (
              <li key={index} className="question-item">
                {question}
              </li>
            ))}
          </ol>
        </div>
      ) : (
        <p>No questions generated yet.</p>
      )}
      <button className="save-button" onClick={handleSaveQuestions} disabled={generatedQuestions.length === 0}>
        Save Questions
      </button>
      <Link to="/">Back to Home</Link>
    </div>
  );
};

export default QuestionDisplay;
