import React, { useState } from 'react';
import axios from 'axios';
import QuestionDisplay from './components/QuestionDisplay';
import FileUploader from './components/FileUploader';
import DifficultySelector from './components/DifficultySelector';
import NumberOfQuestionsInput from './components/NumberOfQuestionsInput';
import './App.css';

function App() {
  const [generatedQuestions, setGeneratedQuestions] = useState([]);
  const [difficulty, setDifficulty] = useState('easy');
  const [numberOfQuestions, setNumberOfQuestions] = useState(1);

  const handleFileUpload = async (file) => {
    const formData = new FormData();
    formData.append('file', file);
    formData.append('difficulty', difficulty);
    formData.append('numberOfQuestions', numberOfQuestions);

    try {
      const response = await axios.post('http://localhost:5000/api/questions/generate', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });
      setGeneratedQuestions(response.data.questions);
    } catch (error) {
      console.error('Error generating questions:', error);
    }
  };

  const handleSaveQuestions = () => {
    const content = generatedQuestions.join('\n');
    const blob = new Blob([content], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'generated_questions.txt';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  return (
    <div className="App">
      <h1>Question Bank Generator</h1>
      <div className="flex-container">
        <div className="flex-item">
          <NumberOfQuestionsInput numberOfQuestions={numberOfQuestions} setNumberOfQuestions={setNumberOfQuestions} />
        </div>
        <div className="flex-item">
          <DifficultySelector difficulty={difficulty} setDifficulty={setDifficulty} />  
        </div>
      </div>
      <div className="file-uploader">
        <FileUploader onFileUpload={handleFileUpload} />
      </div>
      {generatedQuestions.length > 0 && (
        <div className="question-display">
          <QuestionDisplay generatedQuestions={generatedQuestions} />
          <button onClick={handleSaveQuestions}>Save Questions</button>
        </div>
      )}
    </div>
  );
}

export default App;
